var util    = require('util');
var events  = require('events');
function ShimSocket() { events.EventEmitter.call(this); };
util.inherits(ShimSocket, events.EventEmitter);
exports.ShimSocket = ShimSocket;
exports.createSocket = function(type, listener) { return new ShimSocket(type, listener); };
ShimSocket.prototype.bind = function() {};
ShimSocket.prototype.send = function(buffer) { this.emit('data', buffer); };
ShimSocket.prototype.close = function() { this.emit('close'); };
